import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ReviewForm from './ReviewForm';
import Loading from './Loading';
import Error from './Error';

function MovieDetails() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovie = async () => {
      try {
        const movieRes = await axios.get(`/api/movies/${id}`);
        setMovie(movieRes.data);

        const reviewsRes = await axios.get(`/api/reviews/movie/${id}`);
        setReviews(reviewsRes.data);
      } catch (err) {
        setError('Failed to load movie details');
      } finally {
        setLoading(false);
      }
    };

    fetchMovie();
  }, [id]);

  if (loading) return <Loading />;
  if (error) return <Error message={error} />;

  return (
    <div className="movie-details">
      {movie && (
        <>
          {/* Movie Header */}
          <div className="movie-header">
            <div className="movie-poster">
              {movie.posterUrl ? (
                <img src={movie.posterUrl} alt={movie.title} />
              ) : (
                <div className="no-image">No Image Available</div>
              )}
            </div>
            <div className="movie-info">
              <h1>{movie.title || 'N/A'}</h1>
              {movie.tagline && <p className="tagline">"{movie.tagline}"</p>}
              <div className="movie-meta">
                <span>Release Year: {movie.releaseYear || 'N/A'}</span>
                <span>Runtime: {movie.runtime ? `${movie.runtime} min` : 'N/A'}</span>
                <span>Rating: {movie.averageRating ? `${movie.averageRating}/10` : 'N/A'} ({movie.voteCount ? movie.voteCount.toLocaleString() : '0'} votes)</span>
              </div>
              <div className="genres">
                {movie.genre && movie.genre.length > 0 ? (
                  movie.genre.map((genre, index) => (
                    <span key={index} className="genre-tag">{genre}</span>
                  ))
                ) : (
                  <span className="genre-tag">N/A</span>
                )}
              </div>
              <p className="overview">{movie.synopsis || movie.overview || 'No description available.'}</p>
            </div>
          </div>

          {/* Cast Section */}
          <section className="cast-section">
            <h2>Cast</h2>
            <div className="cast-grid">
              {movie.cast && movie.cast.length > 0 ? (
                movie.cast.slice(0, 10).map((actor, index) => (
                  <div key={index} className="cast-card">
                    <div className="cast-image-placeholder">
                      <span>🎭</span>
                    </div>
                    <p>{actor}</p>
                  </div>
                ))
              ) : (
                <p>No cast information available.</p>
              )}
            </div>
          </section>

          {/* Trailers Section */}
          <section className="trailers-section">
            <h2>Trailers</h2>
            <div className="trailers-grid">
              {movie.trailers && movie.trailers.length > 0 ? (
                movie.trailers.map((trailerUrl, index) => (
                  <div key={index} className="trailer-card">
                    <div className="trailer-placeholder">
                      <span>🎬</span>
                      <p>Trailer {index + 1}</p>
                    </div>
                    <a href={trailerUrl} target="_blank" rel="noopener noreferrer" className="btn-primary">
                      Watch Trailer
                    </a>
                  </div>
                ))
              ) : (
                <p>No trailers available.</p>
              )}
            </div>
          </section>

          {/* Reviews Section */}
          <section className="reviews-section">
            <h2>Reviews</h2>
            <ReviewForm movieId={movie._id} onReviewAdded={(newReview) => setReviews([newReview, ...reviews])} />
            <div className="reviews-list">
              {reviews.map(review => (
                <div key={review._id} className="review-card">
                  <div className="review-header">
                    <span className="review-user">{review.user.name}</span>
                    <span className="review-rating">{'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}</span>
                    <span className="review-date">{new Date(review.createdAt).toLocaleDateString()}</span>
                  </div>
                  <p className="review-comment">{review.comment}</p>
                </div>
              ))}
            </div>
          </section>
        </>
      )}
    </div>
  );
}

export default MovieDetails;
