import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import axios from 'axios';
import Loading from './Loading';
import Error from './Error';

function UserProfile() {
  const { user } = useContext(AuthContext);
  const [reviews, setReviews] = useState([]);
  const [watchlist, setWatchlist] = useState([]);
  const [followedReviews, setFollowedReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!user) {
      setLoading(false);
      return;
    }

    const fetchUserData = async () => {
      try {
        const reviewsRes = await axios.get(`/api/reviews/user/${user._id}`);
        const watchlistRes = await axios.get(`/api/users/${user._id}/watchlist`);
        const followedReviewsRes = await axios.get(`/api/users/${user._id}/followed-reviews`);
        setReviews(reviewsRes.data);
        setWatchlist(watchlistRes.data);
        setFollowedReviews(followedReviewsRes.data);
      } catch (err) {
        setError('Failed to load user data');
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [user]);

  if (loading) return <Loading />;
  if (error) return <Error message={error} />;
  if (!user) return <p>Please login to view your profile.</p>;

  return (
    <div className="user-profile">
      <h2>{user.name}'s Profile</h2>
      <p>Email: {user.email}</p>

      <section className="review-history">
        <h3>My Review History</h3>
        {reviews.length === 0 ? (
          <p>No reviews yet.</p>
        ) : (
          <ul>
            {reviews.map(review => (
              <li key={review._id}>
                <strong>{review.movie.title}</strong>: {review.comment} ({review.rating} stars)
              </li>
            ))}
          </ul>
        )}
      </section>

      <section className="followed-reviews">
        <h3>Reviews from Followed Users</h3>
        {followedReviews.length === 0 ? (
          <p>No reviews from followed users yet.</p>
        ) : (
          <ul>
            {followedReviews.map(review => (
              <li key={review._id}>
                <strong>{review.user.name}</strong> reviewed <strong>{review.movie.title}</strong>: {review.comment} ({review.rating} stars)
              </li>
            ))}
          </ul>
        )}
      </section>

      <section className="watchlist">
        <h3>Watchlist</h3>
        {watchlist.length === 0 ? (
          <p>Your watchlist is empty.</p>
        ) : (
          <ul>
            {watchlist.map(movie => (
              <li key={movie._id}>{movie.title}</li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}

export default UserProfile;
