import React from 'react';
import { Link } from 'react-router-dom';
import { dummyShowsData } from '../assets';

function Home() {
  // Get featured movie (first one)
  const featuredMovie = dummyShowsData[0];

  // Get trending movies (top 6 by vote_average)
  const trendingMovies = dummyShowsData
    .sort((a, b) => b.vote_average - a.vote_average)
    .slice(0, 6);

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Welcome to Movie Review Platform</h1>
          <p>Discover, review, and share your favorite movies</p>
          <Link to="/movies" className="btn-primary">Browse Movies</Link>
        </div>
        {featuredMovie && (
          <div className="hero-movie">
            <img src={featuredMovie.poster_path} alt={featuredMovie.title} />
            <div className="hero-movie-info">
              <h2>{featuredMovie.title}</h2>
              <p>{featuredMovie.overview}</p>
              <div className="movie-meta">
                <span>Rating: {featuredMovie.vote_average}/10</span>
                <span>Year: {new Date(featuredMovie.release_date).getFullYear()}</span>
              </div>
              <Link to={`/movies/${featuredMovie.id}`} className="btn-secondary">View Details</Link>
            </div>
          </div>
        )}
      </section>

      {/* Trending Movies Section */}
      <section className="trending-movies">
        <h2>Trending Movies</h2>
        <div className="movies-grid">
          {trendingMovies.map(movie => (
            <div key={movie.id} className="movie-card">
              <img src={movie.poster_path} alt={movie.title} />
              <div className="movie-info">
                <h3>{movie.title}</h3>
                <p>Rating: {movie.vote_average}/10</p>
                <p>Year: {new Date(movie.release_date).getFullYear()}</p>
                <Link to={`/movies/${movie.id}`} className="btn-small">View Details</Link>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Home;
